import React, { useState } from "react";
import AddCircleIcon from '@material-ui/icons/AddCircle';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';

function CreateArea(props){

  const [isExpanded, setExpanded] = useState(false);

  const [note, setNote] = useState({
        title: "",
        content: ""
      });
    
  function handleChange(event) {
    const { name, value } = event.target;

     setNote((prevNote) => {
          return {
            ...prevNote,
            [name]: value
          };
        });
      }

  function submitNote(event){
          props.onAdd(note)
          setNote({
            title: "",
            content: ""
          })
          event.preventDefault();
      }
      
    function expand(){
        setExpanded(true);
    }  

    return(
        <div>
            <form className = "create-note">
                {isExpanded ? <input onChange = {handleChange} value = {note.title} name = "title" placeholder="Title" /> : null}
                <textarea onClick = {expand} onChange = {handleChange} value = {note.content} name="content" placeholder="Take a note..." rows="3" />
               <Zoom in = {true}>
               <Fab onClick = {submitNote}><AddCircleIcon/>
               </Fab>
               </Zoom>
            </form>
        </div>
    );
}

export default CreateArea;
